import urlJoin from "url-join";
import { httpPostSimple } from "./makeRequest.js";
/**
 * Asks the server to fetch the pipeline definition corresponding to the
 * given pipeline ID. Requires cookies, as this is part of generic issuance
 * user authentication.
 */
export async function requestGenericIssuanceGetPipeline(zupassServerUrl, pipelineId, jwt) {
    return httpPostSimple(urlJoin(zupassServerUrl, `/generic-issuance/api/get-pipeline/${pipelineId}`), async (resText) => ({
        value: JSON.parse(resText),
        success: true
    }), { jwt }, true);
}
