import urljoin from "url-join";
import { httpGetSimple } from "./makeRequest.js";
/**
 * Hits an endpoint to download a semaphore protocol group.
 *
 * Never rejects. All information encoded in the resolved response.
 */
export async function requestGenericIssuanceSemaphoreGroup(zupassServerUrl, pipelineId, groupId) {
    return httpGetSimple(urljoin(zupassServerUrl, "/generic-issuance/api/semaphore", pipelineId, groupId), async (resText) => ({
        value: JSON.parse(resText),
        success: true
    }));
}
