'use strict';

var semaphoreInterface = require('./semaphore-interface.json.cjs');

// Minimum supported tree depth.
const MIN_DEPTH = 1;
// Maximum supported tree depth.
const MAX_DEPTH = 32;
const SemaphoreABI = semaphoreInterface.default.abi;
const SemaphoreBytecode = semaphoreInterface.default.bytecode;

exports.MAX_DEPTH = MAX_DEPTH;
exports.MIN_DEPTH = MIN_DEPTH;
exports.SemaphoreABI = SemaphoreABI;
exports.SemaphoreBytecode = SemaphoreBytecode;
